    /* CONTROLADOR */
    private int insertarTarea(String nombreUsuario, String texto) {
        if (!texto.equals("")) {
            boolean correcto = conexion.insertTarea(nombreUsuario, texto);
            return correcto ? 1 : 0;
        } else {
            return 2;
        }

    }

    private boolean actualizarUsuario(String idUsuario, String nombre, String correo, String tipoUsuario) {
        return conexion.editarUsuario(idUsuario, nombre, correo, tipoUsuario);
    }

    private void cambioTareas(ArrayList<Tarea> datosTabla) {

        String estado = "";

        for (int i = 0; i < datosTabla.size(); i++) {
            estado = ((Boolean) tableTareas.getValueAt(i, 0)) ? "hecha" : "activa";
            conexion.cambiarEstado(Integer.toString(datosTabla.get(i).getIdTarea()), estado);
        }

        mostrarInfo("Los cambios se han guardado con éxito");
    }

    private void eliminarTarea(String nombreUsuario, int fila) {
        ArrayList<Tarea> tareas = conexion.getTareasUsuario(nombreUsuario);
        boolean correcto = conexion.eliminarTarea(Integer.toString(tareas.get(fila).getIdTarea()));
        if (correcto) {
            mostrarInfo("La tarea se elimino correctamente");
            rellenarTablaTareas(nombreUsuario);
        } else {
            mostrarError("La tarea no se elimino con exito");
        }
    }

    private void eliminarUsuario(int fila) {
        int idUsuario = getIdUsuario(fila);
        boolean eliminasion = conexion.eliminarUsuario(Integer.toString(idUsuario));

        if (eliminasion) {
            mostrarInfo("El usuario se ha eliminado correctamente");
        } else {
            mostrarError("El usuario no se ha eliminado correctamente");
        }
    }

    private int getIdUsuario(int fila) {
        ArrayList<Usuario> usuarios = conexion.getUsuarios();
        return usuarios.get(fila).getIdUsuario();
    }

    private void mostrarError(String text) {
        JOptionPane.showMessageDialog(this, text, "AVISO", JOptionPane.ERROR_MESSAGE);
    }

    private void mostrarInfo(String text) {
        JOptionPane.showMessageDialog(this, text, "INFORMACIÓN", JOptionPane.INFORMATION_MESSAGE);
    }

    private boolean insertarUsuario(String nombreUsuario, String contraseña, String correo, String fechaNacimiento) {
        return conexion.crearUsuario(nombreUsuario, contraseña, correo, fechaNacimiento);
    }

    private boolean iniciarSesion(String nombre, String contraseña) {
        return conexion.inicioSesión(nombre, contraseña);
    }

    private String getTipoUsuario(String nombreUsuario) {
        return conexion.getTipoUsuario(nombreUsuario);
    }

    private void cambiarVista(String vista) {
        CardLayout card = (CardLayout) panel.getLayout();
        card.show(panel, vista);
    }

    private void rellenarTablaTareas(String nombreUsuario) {

        ArrayList<Tarea> tareas = conexion.getTareasUsuario(nombreUsuario);
        Object datosTabla[][] = new Object[tareas.size()][3];

        for (int i = 0; i < datosTabla.length; i++) {

            datosTabla[i][0] = tareas.get(i).isEstadoBol();
            datosTabla[i][1] = tareas.get(i).getTexto();
            datosTabla[i][2] = tareas.get(i).getFechaInicio();

        }

        cargarDatosTablaTareas(datosTabla);
    }

    private void cargarDatosTablaTareas(Object[][] datos) {
        DefaultTableModel modelo = (DefaultTableModel) tableTareas.getModel();
        modelo.setDataVector(datos, new Object[]{"estado", "texto", "fecha inicio"});
    }

    private void cargarDatosTablaAdmin(Object[][] datos) {
        DefaultTableModel modelo = (DefaultTableModel) adminTabla.getModel();
        modelo.setDataVector(datos, new Object[]{"nombre", "correo", "tipo usuario"});
    }

    private void rellenarTablaAdmin() {
        ArrayList<Usuario> usuarios = conexion.getUsuarios();
        Object datosTabla[][] = new Object[usuarios.size()][3];

        for (int i = 0; i < datosTabla.length; i++) {

            datosTabla[i][0] = usuarios.get(i).getNombre();
            datosTabla[i][1] = usuarios.get(i).getCorreo();
            datosTabla[i][2] = usuarios.get(i).getTipoUsuario();

        }

        cargarDatosTablaAdmin(datosTabla);
    }

    private Usuario getUsuario(int fila) {
        ArrayList<Usuario> usuarios = conexion.getUsuarios();
        return usuarios.get(fila);
    }


