    private void botonAñadirTareasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botonAñadirTareasActionPerformed
        String nombre = UsuarioNombre.getText();
        String text = tareaTexto.getText();
        int returnTarea = insertarTarea(nombre, text);

        if (returnTarea == 1) {
            mostrarInfo("La tarea se añadio exitosamente");
            tareaTexto.setText("");
            rellenarTablaTareas(nombre);
            cambiarVista("panelLista");
        } else if (returnTarea == 0) {
            mostrarError("Hubo un error al crear la tarea");
            tareaTexto.setText("");
        } else {
            mostrarError("La tarea no debe estar vacía");
        }
    }//GEN-LAST:event_botonAñadirTareasActionPerformed

    private void usuarioGuardarCambiosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_usuarioGuardarCambiosActionPerformed
        String nombre = UsuarioNombre.getText();
        ArrayList<Tarea> tareas = conexion.getTareasUsuario(nombre);
        cambioTareas(tareas);
    }//GEN-LAST:event_usuarioGuardarCambiosActionPerformed

    private void usuarioEliminarTareaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_usuarioEliminarTareaActionPerformed
        String nombre = UsuarioNombre.getText();
        int fila = tableTareas.getSelectedRow();
        eliminarTarea(nombre, fila);
    }//GEN-LAST:event_usuarioEliminarTareaActionPerformed

    private void adminEliminarUsuarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_adminEliminarUsuarioActionPerformed
        int fila = adminTabla.getSelectedRow();
        eliminarUsuario(fila);
        rellenarTablaAdmin();
    }//GEN-LAST:event_adminEliminarUsuarioActionPerformed

    private void usuarioGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_usuarioGuardarActionPerformed

        String nombre = usuarioNombre.getText();
        String correo = usuarioCorreo.getText();
        String tipoUsuario = (String) usuarioTipoUsuario.getSelectedItem();
        int idUsuario = getIdUsuario(Integer.parseInt(usuarioFila.getText()));

        boolean updateCorrecto = actualizarUsuario(Integer.toString(idUsuario), nombre, correo, tipoUsuario);

        if (updateCorrecto) {
            mostrarInfo("Los valores del usuario se actualizaron con éxito");
            rellenarTablaAdmin();
            cambiarVista("panelAdmin");
        } else {
            mostrarError("Hubo un error al actualizar los valores del usuario");
        }
    }//GEN-LAST:event_usuarioGuardarActionPerformed

